from django.contrib import adminfrom .models import post, category, profile, Comment# Register your models here.class PostAdmin(admin.ModelAdmin):	list_display = ('title', 'author',  'status')	prepopulated_fields = {"slug": ("title",)}	list_filter = ('status', 'created', 'author')	search_fields = ('title', 'body')	raw_id_fields = ('author',)	publish_hierarchy = 'publish'	ordering = ('status', 'publish')admin.site.register(post, PostAdmin)admin.site.register(category)admin.site.register(profile)@admin.register(Comment)class CommentAdmin(admin.ModelAdmin):	list_display = ('name','date_added', 'body')	list_filter = ('name', 'date_added')	search_fields = ('name', 'body', 'date_added')	