from django.contrib import adminfrom .models import post, category, profile# Register your models here.@admin.register(post)class PostAdmin(admin.ModelAdmin):	list_display = ('title', 'author', 'date', 'status')	list_filter = ('status', 'created', 'date', 'author')	search_fields = ('title', 'body')	raw_id_fields = ('author',)	date_hierarchy = 'date'	ordering = ('status', 'date')admin.site.register(profile)admin.site.register(category)